package fundamentos;

public class Aritmeticos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(23.5+25);
		
		var x = 23.5;
		double y = 2.2;
		
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
		
		int a = 8;
		int b = 3;
		
		System.out.println(a + b);
		System.out.println(a - b);
		System.out.println(a * b);
		System.out.println(a / b);
		
				

	}

}
